import React from "react";
import ReactDom from "react-dom";

const currentDate = new Date();

const currentTime = currentDate.getHours();
let greetings;
const greetingColor = {
  color: "red",
  borderBottom: "4px solid black",
  maxWidth: "230px",
};

if (currentTime < 12) {
  greetings = "GoodMorning";
  greetingColor.color = "red";
  greetingColor.borderColor = "red";
} else if (currentTime < 18) {
  greetings = "GoodAfternoon";
  greetingColor.color = "green";
  greetingColor.borderColor = "green";
} else {
  greetings = "GoodNight";
  greetingColor.color = "blue";
  greetingColor.borderColor = "blue";
}
ReactDom.render(
  <>
    <h1 style={greetingColor}>{greetings}</h1>
  </>,
  document.getElementById("root")
);
