import React from "react";

function Heading() {
  const date = new Date(2023, 1, 1, 13);
  const currentHour = date.getHours();

  let Greetings;

  const customeStyle = {
    color: "",
    borderBottom: "4px solid black",
    width: "380px",
  };

  if (currentHour < 12) {
    Greetings = "GoodMorning";
    customeStyle.color = "red";
    customeStyle.borderColor = "red";
  } else if (currentHour < 18) {
    Greetings = "GoodAfternoon";
    customeStyle.color = "blue";
    customeStyle.borderColor = "blue";
  } else {
    Greetings = "GoodNight";
    customeStyle.color = "green";
    customeStyle.borderColor = "green";
  }

  return (
    <h1 className="heading" style={customeStyle}>
      {`Hello sushant, ${Greetings}`}
    </h1>
  );
}

export default Heading;
